import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const Profile = () => {
  const user = JSON.parse(localStorage.getItem("user"));
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    const fetchUserPosts = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/posts/user/${user.username}`);
        setPosts(res.data);
      } catch (err) {
        console.error("Kullanıcı postları alınamadı:", err);
      }
    };

    fetchUserPosts();
  }, [user.username]);

  return (
    <div style={{ padding: "30px", maxWidth: "800px", margin: "auto" }}>
      <h2>{user.username}'s Profile</h2>
      <h3>My Blog Posts</h3>

      {posts.length === 0 ? (
        <p>You haven't written any posts yet.</p>
      ) : (
        posts.map((post) => (
          <div key={post._id} style={{ marginBottom: "20px", padding: "15px", backgroundColor: "#f9f9f9", borderRadius: "8px" }}>
            <h4>{post.title}</h4>
            <p><strong>Category:</strong> {post.category}</p>
            <p><strong>Created:</strong> {new Date(post.createdAt).toLocaleDateString()}</p>
            <Link to={`/posts/${post._id}`}>
              <button style={{ marginRight: "10px" }}>View</button>
            </Link>
            <Link to={`/edit/${post._id}`}>
              <button>Edit</button>
            </Link>
          </div>
        ))
      )}
    </div>
  );
};

export default Profile;
