import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const AllPosts = () => {
  const [posts, setPosts] = useState([]);
  const [category, setCategory] = useState(""); // SeÃ§ili kategori

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const res = await axios.get(
          `http://localhost:5000/api/posts${category ? `?category=${category}` : ""}`
        );
        setPosts(res.data);
      } catch (err) {
        console.error("Failed to fetch posts:", err);
      }
    };
    fetchPosts();
  }, [category]);

  const categories = ["technology", "health", "education", "travel"]; // Ã¶rnek

  return (
    <div style={{ padding: "30px", maxWidth: "800px", margin: "auto" }}>
      <h2>ðŸ“š All Blog Posts</h2>

      <div style={{ marginBottom: "20px" }}>
        <label>Select category: </label>
        <select value={category} onChange={(e) => setCategory(e.target.value)}>
          <option value="">All</option>
          {categories.map((cat) => (
            <option key={cat}>{cat}</option>
          ))}
        </select>
      </div>

      {posts.length === 0 ? (
        <p>No posts found.</p>
      ) : (
        posts.map((post) => (
          <div
            key={post._id}
            style={{
              border: "1px solid #ddd",
              borderRadius: "10px",
              padding: "15px",
              marginBottom: "20px",
              boxShadow: "0px 1px 5px rgba(0,0,0,0.1)"
            }}
          >
            <Link to={`/posts/${post._id}`} style={{ textDecoration: "none", color: "black" }}>
              <h3>{post.title}</h3>
            </Link>
            <p><strong>Category:</strong> {post.category}</p>
            <p><strong>Author:</strong> {post.author}</p>
            <p>{post.content.slice(0, 150)}...</p>
            <Link to={`/posts/${post._id}`}>
              <button style={{ marginTop: "10px" }}>Read More</button>
            </Link>
          </div>
        ))
      )}
    </div>
  );
};

export default AllPosts;
