import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";

const PostDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [post, setPost] = useState(null);
  const user = JSON.parse(localStorage.getItem("user")); // ✅ kullanıcıyı tanımla

  useEffect(() => {
    const fetchPost = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/posts/${id}`);
        setPost(res.data);
      } catch (err) {
        console.error("Error fetching post:", err);
      }
    };

    fetchPost();
  }, [id]);

  const handleDelete = async () => {
    const confirmDelete = window.confirm("Are you sure you want to delete this post?");
    if (!confirmDelete) return;

    try {
      await axios.delete(`http://localhost:5000/api/posts/${id}`, {
        data: { author: user?.username }, // kullanıcının adı ile kontrol
      });
      alert("Post deleted successfully.");
      navigate("/");
    } catch (err) {
      console.error("Error deleting post:", err);
      alert(err.response?.data || "An error occurred while deleting the post.");
    }
  };

  if (!post) return <p>Loading...</p>;

  return (
    <div style={{ padding: "30px", maxWidth: "800px", margin: "auto" }}>
      <h2>{post.title}</h2>
      <p><strong>Category:</strong> {post.category}</p>
      <p><strong>Author:</strong> {post.author}</p>
      <p>{post.content}</p>

      {user?.username === post.author && (
        <button onClick={handleDelete} style={{ marginTop: "20px", backgroundColor: "red", color: "white" }}>
          Delete This Post
        </button>
      )}
    </div>
  );
};

export default PostDetail;
