import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";

const PostDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [post, setPost] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchPost = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/posts/${id}`);
        setPost(res.data);
        setLoading(false);
      } catch (err) {
        console.error("Error fetching post:", err);
        setLoading(false);
      }
    };
    fetchPost();
  }, [id]);

  const handleDelete = async () => {
  const confirmDelete = window.confirm("Are you sure you want to delete this post?");
  if (!confirmDelete) return;

  try {
    await axios.delete(`http://localhost:5000/api/posts/${id}`, {
      data: { author: user.username } // burada oturumdaki kullanıcıyı gönderiyorsun
    });
    alert("Post deleted successfully.");
    navigate("/");
  } catch (err) {
    console.error("Error deleting post:", err);
    alert(err.response?.data || "An error occurred while deleting the post.");
  }
};


  if (loading) return <p>Loading...</p>;
  if (!post) return <p>Post not found</p>;

  return (
    <div style={{ padding: "30px", maxWidth: "800px", margin: "auto" }}>
      <h2>{post.title}</h2>
      <p><strong>Category:</strong> {post.category}</p>
      <p><strong>Author:</strong> {post.author}</p>
      <p>{post.content}</p>
      
      <button onClick={handleDelete} style={{ marginTop: "20px", backgroundColor: "red", color: "white", border: "none", padding: "10px 20px", borderRadius: "5px" }}>
        Delete Post
      </button>
    </div>
  );
};

export default PostDetail;
